import { Injectable } from '@angular/core';
import * as pdfMake from 'pdfmake/build/pdfmake';
import * as pdfFonts from 'pdfmake/build/vfs_fonts';

(<any>pdfMake).vfs = pdfFonts.pdfMake.vfs;

@Injectable({
  providedIn: 'root'
})
export class GeneratePdfService {
  constructor() { }

  async  generatePDF(bonCommande: any, produitsOfferts: any[]) {
    try {
      const documentDefinition = await this.getDocumentDefinition(
        bonCommande,
        produitsOfferts
      );
      pdfMake.createPdf(documentDefinition).open();
    } catch (error) {
      console.error('Erreur lors de la génération du PDF :', error);
    }
  }

 async getDocumentDefinition(bonCommande: any, produitsOfferts: any[]) {
  const productDetails = await this.generateProductDetails(produitsOfferts);

    return {

      content: [
        {
          image:"data:image/png;base64,", 
          width: 150, // Ajustez la taille du logo
          alignment: 'center',
          margin: [0, 0, 0, 20] // Ajouter un espace sous le logo
        },
        {
          text: `Bon de commande : ${bonCommande.numero || 'Non disponible'}`,
          style: 'header',
          alignment: 'center',
        },
        { text: 'Détails du Fournisseur', style: 'subheader' },
        {
          table: {
            widths: ['*', '*'],
            body: this.generateSupplierDetails(produitsOfferts),
          },
          layout: 'lightHorizontalLines',
        },
        { text: 'Détails du Bon de Commande', style: 'subheader' },
        {
          table: {
            widths: ['*', '*'],
            body: this.generateCommandeDetails(bonCommande),
          },
          layout: 'lightHorizontalLines',
        },
        { text: 'Détails de Livraison', style: 'subheader' },
        {
          table: {
            widths: ['*', '*'],
            body: this.generateLivraisonDetails(bonCommande),
          },
          layout: 'lightHorizontalLines',
        },
        { text: 'Détails des Produits Offerts', style: 'subheader' },
        productDetails,
      ],
      styles: {
        header: {
          fontSize: 18,
          bold: true,
          color: '#3F51B5', // Bleu professionnel
          margin: [0, 0, 0, 10],
        },
        subheader: {
          fontSize: 14,
          bold: true,
          color: '#303F9F', // Bleu foncé
          margin: [0, 10, 0, 5],
        },
        itemText: {
          margin: [0, 5, 0, 5],
        },
        tableHeader: {
          bold: true,
          fontSize: 12,
          color: '#FFFFFF', // Texte blanc
          fillColor: '#546E7A', // Gris-bleu
        },
      },
      footer: (currentPage, pageCount) => {
        return {
          text: `Page ${currentPage} of ${pageCount}`,
          alignment: 'center',
          margin: [0, 10, 0, 0],
          fontSize: 10,
          color: '#616161', // Gris clair
        };
      },
      pageSize: 'A4',
      pageMargins: [40, 60, 40, 60],
    };
  }

  generateSupplierDetails(produitsOfferts: any[]) {
    if (!produitsOfferts || produitsOfferts.length === 0) {
      return [['Aucun fournisseur disponible.']];
    }

    const premierProduit = produitsOfferts[0];
    const fournisseur = premierProduit?.offre?.fournisseur || {};

    return [
      [{ text: 'Nom du fournisseur', style: 'tableHeader' }, fournisseur.nom || 'Non disponible'],
      [{ text: 'Email du fournisseur', style: 'tableHeader' }, fournisseur.email || 'Non disponible'],
      [{ text: 'Téléphone du fournisseur', style: 'tableHeader' }, fournisseur.tel || 'Non disponible'],
      [{ text: 'Adresse du fournisseur', style: 'tableHeader' }, fournisseur.adresse || 'Non disponible'],
    ];
  }

  generateCommandeDetails(bonCommande: any) {
    return [
      [{ text: 'Numéro de commande', style: 'tableHeader' }, bonCommande.numero || 'Non disponible'],
      [{ text: 'Référence du bon de commande', style: 'tableHeader' }, bonCommande.reference || 'Non disponible'],
    ];
  }

  generateLivraisonDetails(bonCommande: any) {
    return [
      [{ text: 'Délai de livraison', style: 'tableHeader' }, bonCommande.delailivraison || 'Non disponible'],
      [{ text: 'Modalités de livraison', style: 'tableHeader' }, bonCommande.typelivraison || 'Non disponible'],
      [{ text: 'Adresse de livraison', style: 'tableHeader' }, bonCommande.adresselivraison || 'Non disponible'],
    ];
  }

  async generateProductDetails(produitsOfferts: any[]): Promise<any> {
    if (!produitsOfferts || produitsOfferts.length === 0) {
      return { text: 'Aucun produit offert.', style: 'itemText' };
    }
  
    // Entête des colonnes du tableau
    const headerRow = [
      { text: 'Nom du Produit', style: 'tableHeader' },
      { text: 'Description', style: 'tableHeader' },
      { text: 'Quantité', style: 'tableHeader' },
      { text: 'Prix', style: 'tableHeader' },
      { text: 'Total', style: 'tableHeader' },
      { text: 'Image', style: 'tableHeader' },
    ];
  
    // Corps des lignes, avec gestion des images
    const bodyRows = await Promise.all(
      produitsOfferts.map(async (produit) => {
        const imagePath = `assets/img/${produit.img || 'placeholder.png'}`;
        const base64Image = await this.loadImageAsBase64(imagePath).catch(() => '');
  
        return [
          produit.nom || 'Non disponible',
          produit.description || 'Non disponible',
          produit.quantite || 0,
          `${produit.prix || 0} DT`,
          `${((produit.quantite || 0) * (produit.prix || 0)).toFixed(2)} DT`,
          base64Image
            ? { image: base64Image, width: 50, height: 50 }
            : { text: 'Image non disponible', style: 'itemText' },
        ];
      })
    );
  
    return {
      table: {
        headerRows: 1,
        widths: ['*', '*', 'auto', 'auto', 'auto', 50], // Largeur ajustée pour l'image
        body: [headerRow, ...bodyRows],
      },
      layout: 'lightHorizontalLines', // Un design léger et propre
      style: 'itemText',
    };
  }
  


  loadImageAsBase64(path: string): Promise<string> {
    return new Promise((resolve, reject) => {
      const xhr = new XMLHttpRequest();
      xhr.onload = function () {
        const reader = new FileReader();
        reader.onloadend = function () {
          resolve(reader.result as string);
        };
        reader.readAsDataURL(xhr.response);
      };
      xhr.onerror = function () {
        reject('Erreur lors du chargement de l\'image.');
      };
      xhr.open('GET', path);
      xhr.responseType = 'blob';
      xhr.send();
    });
  }
  
}
